	function initTouch(){
		var startX,
			startY,
			touchStartX,
			touchStartY,
			moved,
			moving = false;

		thumbHolder.unbind('touchstart.ap touchmove.ap touchend.ap click.ap-touchclick').bind(
			'touchstart.ap',
			function(e){
				if(!componentInited || categoryTransitionOn) return false;
				if(!touchOn){//if touch disabled we want click executed
					return true;
				}
				var touch = e.originalEvent.touches[0];
				startX = thumbHolder.position().left;
				startY = thumbHolder.position().top;
				touchStartX = touch.pageX;
				touchStartY = touch.pageY;
				moved = false;
				moving = true;
			}
		).bind(
			'touchmove.ap',
			function(ev){
				if(!moving){
					return;
				}
				var touchPos = ev.originalEvent.touches[0];
				if(thumbsHorizontal){
					var value = startX - touchStartX + touchPos.pageX, w = getComponentSize('w');
					
					//toggle advance buttons
					if(value > 0){
						value=0;	
						//togglePrevBtn('off');
					}else{
						//togglePrevBtn('on');
					}
					if(value < w - allThumbsSize - scrollDifference){
						value=w - allThumbsSize - scrollDifference;	
						//toggleNextBtn('off');
					}else{
						//toggleNextBtn('on');
					}
								
					thumbHolder.css('left',value+'px');
				}else{
					var value=startY - touchStartY + touchPos.pageY, h = getComponentSize('h');
					
					//toggle advance buttons
					if(value > 0){
						value=0;	
						//togglePrevBtn('off');
					}else{
						//togglePrevBtn('on');
					}
					if(value < h - allThumbsSize - scrollDifference){
						value=h - allThumbsSize - scrollDifference;	
						//toggleNextBtn('off');
					}else{
						//toggleNextBtn('on');
					}
					
					thumbHolder.css('top',value+'px');
				}
				moved = moved || Math.abs(touchStartX - touchPos.pageX) > 5 || Math.abs(touchStartY - touchPos.pageY) > 5;
				
				return false;
			}
		).bind(
			'touchend.ap',
			function(e){
				moving = false;
			}
		).bind(
			'click.ap-touchclick',
			function(e){
				if(moved) {
					moved = false;
					return false;
				}
			}
		);
	}
	
