				if(data.attr('data-localPreview') != undefined)preview = data.attr('data-localPreview');
				videoLink = {'mp4': mp4, 'ogv': ogv, 'webm': webm, 'preview': preview};
				toggleVideoBtn('on');
				videoExistInSlide=true;
